class A {
  routine new {
    x := 3
    y := 6
  }

  var x:Int32
  var y:Int32
}

a := A
echo a.x * a.y


class A {
  routine new(_x:Int32, _y:Int32) {
    x := _x
    y := _y
  }

  var x:Int32
  var y:Int32
}

a := A(4, 7)
echo a.x * a.y


class A {
  routine new(x:Int32, y:Int32) {
    this.x := x
    this.y := y
  }

  var x:Int32
  var y:Int32
}

a := A(5, 8)
echo a.x * a.y

class A { var x := 11 var y := 22
operator +(other:A) { var ret := A ret.x := x + other.x ret.y := y + other.y return ret } }
var a := A var b := A var c := a + b echo c.x echo c.y


