class A { public var a := 10 private var b := 20
public routine foo :: 30 private routine ber :: 40 }
A() =: var o echo o.a echo o.b echo o.foo echo o.ber


class A
{
  @public var a := 10
  @private var b := 20

  @public routine foo :: 30
  @private routine ber :: 40
}

A() =: var o
echo o.a
echo o.b
echo o.foo
echo o.ber

class A { #private var a := 10 var b := 20
routine foo :: 30 routine ber :: 40 }
A() =: var o echo o.a echo o.b echo o.foo echo o.ber

class A { #private public var a := 10 var b := 20
public routine foo :: 30 routine ber :: 40 }
A() =: var o echo o.a echo o.b echo o.foo echo o.ber

class A { public var a := 10 public static var b := 20
public routine foo :: 30 public static routine ber :: 40 }
A() =: var o echo o.a echo A.b echo o.foo echo A.ber

class A { var x := 11 var y := 22
operator +(other:A) { var ret := A ret.x := x + other.x ret.y := y + other.y return ret } }
var a := A var b := A var c := a + b echo c.x echo c.y


